SELECT DISTINCT `messages`.`created_on`, `messages`.`id` FROM `messages` INNER JOIN `boards` ON `boards`.`id` = `messages`.`board_id` INNER JOIN `projects` ON `projects`.`id` = `boards`.`project_id` INNER JOIN `attachments` ON `attachments`.`container_id` = `messages`.`id` AND `attachments`.`container_type` = 'Message' WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='boards')) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13)))))) AND (boards.project_id IN (1)) AND (((attachments.filename LIKE '%search_attachments%') OR (attachments.description LIKE '%search_attachments%'))) ORDER BY `messages`.`created_on` DESC, `messages`.`id` DESC