SELECT `documents`.* FROM `documents` INNER JOIN `projects` ON `projects`.`id` = `documents`.`project_id` WHERE `documents`.`project_id` = 3 AND (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='documents')) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13)))))) AND `documents`.`title` = 'Test document' LIMIT 1