SELECT DISTINCT `changesets`.`committed_on`, `changesets`.`id` FROM `changesets` INNER JOIN `repositories` ON `repositories`.`id` = `changesets`.`repository_id` INNER JOIN `projects` ON `projects`.`id` = `repositories`.`project_id` WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='repository')) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13)))))) AND (repositories.project_id IN (1,5,6,3,4)) AND (((comments LIKE '%recipe%')) OR ((comments LIKE '%subproject%'))) ORDER BY `changesets`.`committed_on` DESC, `changesets`.`id` DESC