SELECT DISTINCT `projects`.`created_on`, `projects`.`id` FROM `projects` WHERE (((projects.status <> 9) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13)))))) AND (projects.id IN (1,5,6,3,4)) AND (((name LIKE '%recipe%') OR (identifier LIKE '%recipe%') OR (description LIKE '%recipe%')) OR ((name LIKE '%subproject%') OR (identifier LIKE '%subproject%') OR (description LIKE '%subproject%'))) ORDER BY `projects`.`created_on` DESC, `projects`.`id` DESC