SELECT `issues`.* FROM `issues` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND ((projects.id IN (1,2,5) AND ((issues.is_private = FALSE OR issues.author_id = 8 OR issues.assigned_to_id IN (8,10,11)))) OR (projects.id IN (5) AND (1=1)))))