SELECT MAX(`journals`.`id`) AS maximum_id, `journals`.`journalized_id` AS journals_journalized_id FROM `journals` INNER JOIN `issues` ON `issues`.`id` = `journals`.`journalized_id` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` WHERE `journals`.`journalized_type` = 'Issue' AND `journals`.`journalized_id` IN (3, 2, 1) AND ((journals.private_notes = FALSE OR journals.user_id = 2 OR (((projects.status <> 9) AND (projects.id IN (1,5)))))) GROUP BY `journals`.`journalized_id`