SELECT MIN(`versions`.`effective_date`) FROM `versions` INNER JOIN `projects` ON `projects`.`id` = `versions`.`project_id` WHERE (projects.id = 1957 OR (projects.status <> 9 AND ( versions.sharing = 'system' OR (projects.lft >= 13 AND projects.rgt <= 14 AND versions.sharing = 'tree') OR (projects.lft < 13 AND projects.rgt > 14 AND versions.sharing IN ('hierarchy', 'descendants')) OR (projects.lft > 13 AND projects.rgt < 14 AND versions.sharing = 'hierarchy'))))