SELECT SUM(`issues`.`estimated_hours`) FROM `issues` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` WHERE `issues`.`root_id` = 3 AND (issues.lft >= 1 AND issues.rgt <= 4) AND (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (2,12))) AND ((issues.is_private = FALSE OR issues.author_id = 2 OR issues.assigned_to_id IN (2)))) OR (projects.id IN (1,5) AND (1=1)) OR (projects.id IN (2) AND ((issues.is_private = FALSE OR issues.author_id = 2 OR issues.assigned_to_id IN (2)))))))