SELECT `changesets`.* FROM `changesets` INNER JOIN `repositories` ON `repositories`.`id` = `changesets`.`repository_id` INNER JOIN `projects` ON `projects`.`id` = `repositories`.`project_id` WHERE (projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='repository')) AND `changesets`.`repository_id` = 10 AND `changesets`.`revision` = '2' LIMIT 1