SELECT SUM(`time_entries`.`hours`) AS sum_hours, parent.id AS parent_id FROM `time_entries` INNER JOIN `projects` ON `projects`.`id` = `time_entries`.`project_id` INNER JOIN `issues` ON `issues`.`id` = `time_entries`.`issue_id` JOIN issues parent ON parent.root_id = issues.root_id AND parent.lft <= issues.lft AND parent.rgt >= issues.rgt WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='time_tracking')) AND ((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (3,12))) OR projects.id IN (1)))) AND (parent.id IN (3337)) GROUP BY parent.id