SELECT COUNT(*) AS count_all, `issues`.`tracker_id` AS issues_tracker_id FROM `issues` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` INNER JOIN `issue_statuses` ON `issue_statuses`.`id` = `issues`.`status_id` WHERE `issues`.`fixed_version_id` = 4 AND (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (7,12))) AND ((issues.is_private = FALSE OR issues.author_id = 7 OR issues.assigned_to_id IN (7))))))) AND `issue_statuses`.`is_closed` = FALSE GROUP BY `issues`.`tracker_id`