SELECT `issue_relations`.* FROM `issue_relations` INNER JOIN `issues` ON `issues`.`id` = `issue_relations`.`issue_from_id` INNER JOIN `projects` ON `projects`.`id` = `issues`.`project_id` WHERE (((projects.status <> 9 AND EXISTS (SELECT 1 AS one FROM enabled_modules em WHERE em.project_id = projects.id AND em.name='issue_tracking')) AND (((projects.is_public = TRUE AND projects.id NOT IN (SELECT project_id FROM members WHERE user_id IN (6,13))) AND ((issues.is_private = FALSE)))))) AND `issue_relations`.`issue_to_id` IN (13, 7, 5, 3, 2, 1)