SELECT `refinery_pages`.* FROM `refinery_pages` INNER JOIN `refinery_page_translations` ON `refinery_page_translations`.`refinery_page_id` = `refinery_pages`.`id` WHERE `refinery_pages`.`parent_id` IS NULL AND `refinery_page_translations`.`locale` IN ('en', 'ru') AND `refinery_page_translations`.`slug` = 'новости' ORDER BY `refinery_pages`.`id` ASC LIMIT 1