SELECT COUNT(*) AS count_all, `locale` AS locale, `refinery_pages`.`parent_id` AS refinery_pages_parent_id, `refinery_page_translations`.`slug` AS refinery_page_translations_slug FROM `refinery_pages` INNER JOIN `refinery_page_translations` ON `refinery_page_translations`.`refinery_page_id` = `refinery_pages`.`id` GROUP BY `locale`, `refinery_pages`.`parent_id`, `refinery_page_translations`.`slug` HAVING COUNT(`refinery_page_translations`.`slug`) > 1