SELECT `spree_variants`.`id` AS t0_r0, `spree_variants`.`sku` AS t0_r1, `spree_variants`.`weight` AS t0_r2, `spree_variants`.`height` AS t0_r3, `spree_variants`.`width` AS t0_r4, `spree_variants`.`depth` AS t0_r5, `spree_variants`.`deleted_at` AS t0_r6, `spree_variants`.`is_master` AS t0_r7, `spree_variants`.`product_id` AS t0_r8, `spree_variants`.`cost_price` AS t0_r9, `spree_variants`.`position` AS t0_r10, `spree_variants`.`cost_currency` AS t0_r11, `spree_variants`.`track_inventory` AS t0_r12, `spree_variants`.`tax_category_id` AS t0_r13, `spree_variants`.`updated_at` AS t0_r14, `spree_variants`.`created_at` AS t0_r15, `spree_assets`.`id` AS t1_r0, `spree_assets`.`viewable_type` AS t1_r1, `spree_assets`.`viewable_id` AS t1_r2, `spree_assets`.`attachment_width` AS t1_r3, `spree_assets`.`attachment_height` AS t1_r4, `spree_assets`.`attachment_file_size` AS t1_r5, `spree_assets`.`position` AS t1_r6, `spree_assets`.`attachment_content_type` AS t1_r7, `spree_assets`.`attachment_file_name` AS t1_r8, `spree_assets`.`type` AS t1_r9, `spree_assets`.`attachment_updated_at` AS t1_r10, `spree_assets`.`alt` AS t1_r11, `spree_assets`.`created_at` AS t1_r12, `spree_assets`.`updated_at` AS t1_r13 FROM `spree_variants` LEFT OUTER JOIN `spree_assets` ON `spree_assets`.`viewable_type` = 'Spree::Variant' AND `spree_assets`.`viewable_id` = `spree_variants`.`id` AND `spree_assets`.`type` = 'Spree::Image' WHERE `spree_variants`.`deleted_at` IS NULL AND `spree_assets`.`type` = 'Spree::Image' AND `spree_variants`.`product_id` IN (247, 248) ORDER BY `spree_variants`.`position` ASC, `spree_assets`.`position` ASC