SELECT `spree_promotions`.* FROM `spree_promotions` INNER JOIN `spree_promotion_actions` ON `spree_promotion_actions`.`deleted_at` IS NULL AND `spree_promotion_actions`.`promotion_id` = `spree_promotions`.`id` WHERE (`spree_promotions`.`starts_at` IS NULL OR `spree_promotions`.`starts_at` < '2020-05-16 05:23:59') AND (`spree_promotions`.`expires_at` IS NULL OR `spree_promotions`.`expires_at` > '2020-05-16 05:23:59') AND `spree_promotion_actions`.`deleted_at` IS NULL AND `spree_promotion_actions`.`type` = 'Spree::Promotion::Actions::FreeShipping' AND `spree_promotions`.`apply_automatically` = TRUE