SELECT `spree_stock_movements`.* FROM `spree_stock_movements` INNER JOIN `spree_stock_items` ON `spree_stock_movements`.`stock_item_id` = `spree_stock_items`.`id` WHERE `spree_stock_items`.`deleted_at` IS NULL AND `spree_stock_items`.`stock_location_id` = 49 AND (TRUE=FALSE) AND `spree_stock_movements`.`id` = 20 LIMIT 1