SELECT `spree_variants`.* FROM `spree_variants` WHERE `spree_variants`.`deleted_at` IS NULL AND EXISTS (SELECT `spree_prices`.* FROM `spree_prices` WHERE `spree_prices`.`deleted_at` IS NULL AND `spree_variants`.`id` = `spree_prices`.`variant_id` AND (spree_prices.currency = 'USD' AND (spree_prices.country_iso IS NULL OR spree_prices.country_iso = NULL))) AND `spree_variants`.`sku` = 'TEST-1' LIMIT 1