SELECT `items`.* FROM `spree_stock_items` items WHERE `items`.`deleted_at` IS NULL AND `items`.`stock_location_id` = 26 AND `items`.`id` IN ( SELECT `i`.`id` FROM `spree_stock_items` i LEFT OUTER JOIN `spree_stock_locations` ON `spree_stock_locations`.`id` = `i`.`stock_location_id` WHERE `spree_stock_locations`.`active` = TRUE ) AND `items`.`id` = 68 LIMIT 1