SELECT `spree_variants`.* FROM `spree_variants` INNER JOIN `spree_prices` ON `spree_prices`.`deleted_at` IS NULL AND `spree_prices`.`variant_id` = `spree_variants`.`id` WHERE `spree_variants`.`deleted_at` IS NULL AND `spree_variants`.`product_id` = 908 AND `spree_variants`.`is_master` = FALSE AND `spree_variants`.`deleted_at` IS NULL AND `spree_prices`.`currency` = 'USD' AND (spree_prices.amount IS NOT NULL) ORDER BY `spree_variants`.`position` ASC