SELECT `spree_products`.* FROM `spree_products` WHERE `spree_products`.`deleted_at` IS NULL AND EXISTS (SELECT `spree_variants`.* FROM `spree_variants` WHERE `spree_variants`.`deleted_at` IS NULL AND `spree_variants`.`sku` LIKE '%A%' AND `spree_variants`.`product_id` = `spree_products`.`id`) ORDER BY `spree_products`.`name` ASC, `spree_products`.`id` ASC LIMIT 10 OFFSET 0