SELECT COUNT(DISTINCT `spree_products`.`id`) FROM `spree_products` INNER JOIN `spree_variants` ON `spree_variants`.`is_master` = TRUE AND `spree_variants`.`product_id` = `spree_products`.`id` WHERE `spree_products`.`deleted_at` IS NULL AND EXISTS (SELECT `spree_prices`.* FROM `spree_prices` WHERE `spree_prices`.`deleted_at` IS NULL AND `spree_variants`.`id` = `spree_prices`.`variant_id`) AND (`spree_products`.available_on <= '2020-05-16 05:06:56.715672') AND `spree_products`.`name` LIKE '%fritos%'