SELECT `spree_promotions`.* FROM `spree_promotions` INNER JOIN `spree_orders_promotions` ON `spree_orders_promotions`.`promotion_id` = `spree_promotions`.`id` WHERE (`spree_promotions`.`starts_at` IS NULL OR `spree_promotions`.`starts_at` < '2020-05-16 05:16:59') AND (`spree_promotions`.`expires_at` IS NULL OR `spree_promotions`.`expires_at` > '2020-05-16 05:16:59') AND `spree_orders_promotions`.`order_id` = 1205