SELECT DISTINCT spree_products.*, spree_products_taxons.position FROM `spree_products` INNER JOIN `spree_products_taxons` ON `spree_products`.`id` = `spree_products_taxons`.`product_id` INNER JOIN `spree_variants` ON `spree_variants`.`is_master` = TRUE AND `spree_variants`.`product_id` = `spree_products`.`id` INNER JOIN `spree_variants` `variants_including_masters_spree_products_join` ON `variants_including_masters_spree_products_join`.`deleted_at` IS NULL AND `variants_including_masters_spree_products_join`.`product_id` = `spree_products`.`id` INNER JOIN `spree_prices` ON `spree_prices`.`deleted_at` IS NULL AND `spree_prices`.`variant_id` = `variants_including_masters_spree_products_join`.`id` WHERE `spree_products`.`deleted_at` IS NULL AND `spree_products_taxons`.`taxon_id` = 201 AND (`spree_products`.deleted_at IS NULL or `spree_products`.deleted_at >= '2020-05-16 05:20:18.095395') AND EXISTS (SELECT `spree_prices`.* FROM `spree_prices` WHERE `spree_prices`.`deleted_at` IS NULL AND `spree_variants`.`id` = `spree_prices`.`variant_id`) AND (`spree_products`.available_on <= '2020-05-16 05:20:18.095650') AND `spree_prices`.`deleted_at` IS NULL AND `spree_prices`.`currency` = 'USD' AND `spree_prices`.`country_iso` IS NULL AND `spree_products`.`id` NOT IN (2133, 2134) ORDER BY `spree_products_taxons`.`position` ASC LIMIT 2