SELECT `spree_stock_items`.* FROM `spree_stock_items` LEFT OUTER JOIN `spree_stock_locations` ON `spree_stock_locations`.`id` = `spree_stock_items`.`stock_location_id` WHERE `spree_stock_items`.`deleted_at` IS NULL AND `spree_stock_items`.`stock_location_id` = 23 AND `spree_stock_locations`.`active` = TRUE LIMIT 25 OFFSET 0