SELECT DISTINCT spree_products.*, spree_prices.amount FROM `spree_products` INNER JOIN `spree_variants` ON `spree_variants`.`deleted_at` IS NULL AND `spree_variants`.`product_id` = `spree_products`.`id` AND `spree_variants`.`is_master` = TRUE INNER JOIN `spree_prices` ON `spree_prices`.`deleted_at` IS NULL AND `spree_prices`.`variant_id` = `spree_variants`.`id` INNER JOIN `spree_prices` `default_prices_spree_variants` ON `default_prices_spree_variants`.`deleted_at` IS NULL AND `default_prices_spree_variants`.`variant_id` = `spree_variants`.`id` AND `default_prices_spree_variants`.`currency` = 'USD' WHERE `spree_products`.`deleted_at` IS NULL AND (`spree_products`.deleted_at IS NULL or `spree_products`.deleted_at >= '2020-05-01 07:38:38.669146') AND (`spree_products`.discontinue_on IS NULL or `spree_products`.discontinue_on >= '2020-05-01 07:38:38.669409') AND (`spree_products`.available_on <= '2020-05-01 07:38:38.669392') ORDER BY `spree_prices`.amount DESC