SELECT `spree_stock_movements`.* FROM `spree_stock_movements` INNER JOIN `spree_stock_items` ON `spree_stock_items`.`deleted_at` IS NULL AND `spree_stock_items`.`id` = `spree_stock_movements`.`stock_item_id` WHERE `spree_stock_movements`.`originator_id` = 2 AND `spree_stock_movements`.`originator_type` = 'Spree::StockTransfer' AND `spree_stock_items`.`stock_location_id` = 1996 ORDER BY `spree_stock_movements`.`id` ASC LIMIT 1