SELECT `spree_option_types`.`id` AS t0_r0, `spree_option_types`.`name` AS t0_r1, `spree_option_types`.`presentation` AS t0_r2, `spree_option_types`.`position` AS t0_r3, `spree_option_types`.`created_at` AS t0_r4, `spree_option_types`.`updated_at` AS t0_r5, `spree_option_values`.`id` AS t1_r0, `spree_option_values`.`position` AS t1_r1, `spree_option_values`.`name` AS t1_r2, `spree_option_values`.`presentation` AS t1_r3, `spree_option_values`.`option_type_id` AS t1_r4, `spree_option_values`.`created_at` AS t1_r5, `spree_option_values`.`updated_at` AS t1_r6 FROM `spree_option_types` LEFT OUTER JOIN `spree_option_values` ON `spree_option_values`.`option_type_id` = `spree_option_types`.`id` ORDER BY spree_option_types.position ASC, spree_option_values.position ASC