SELECT `spree_variants`.* FROM `spree_variants` INNER JOIN `spree_option_value_variants` ON `spree_variants`.`id` = `spree_option_value_variants`.`variant_id` WHERE `spree_variants`.`deleted_at` IS NULL AND `spree_option_value_variants`.`option_value_id` = 696 AND `spree_variants`.`id` IN (2474, 2475, 2476) ORDER BY `spree_variants`.`position` ASC LIMIT 1