SELECT `spree_products`.* FROM `spree_products` INNER JOIN `spree_product_properties` ON `spree_product_properties`.`product_id` = `spree_products`.`id` INNER JOIN `spree_properties` ON `spree_properties`.`id` = `spree_product_properties`.`property_id` WHERE `spree_products`.`deleted_at` IS NULL AND `spree_properties`.`name` = 'brand' AND `spree_product_properties`.`value` = 'Nike'