SELECT `spree_adjustments`.* FROM `spree_adjustments` WHERE `spree_adjustments`.`adjustable_id` = 1 AND `spree_adjustments`.`adjustable_type` = 'Spree::Order' AND `spree_adjustments`.`source_type` = 'Spree::TaxRate' AND `spree_adjustments`.`source_id` = 1 AND `spree_adjustments`.`order_id` = 1 AND `spree_adjustments`.`label` = 'Tax' AND `spree_adjustments`.`state` = 'open' AND `spree_adjustments`.`mandatory` = TRUE ORDER BY `spree_adjustments`.`created_at` ASC LIMIT 1