SELECT DISTINCT spree_products.*, spree_prices.amount FROM `spree_products` INNER JOIN `spree_variants` ON `spree_variants`.`deleted_at` IS NULL AND `spree_variants`.`product_id` = `spree_products`.`id` AND `spree_variants`.`is_master` = TRUE INNER JOIN `spree_prices` ON `spree_prices`.`deleted_at` IS NULL AND `spree_prices`.`variant_id` = `spree_variants`.`id` WHERE `spree_products`.`deleted_at` IS NULL AND (`spree_products`.deleted_at IS NULL or `spree_products`.deleted_at >= '2020-05-01 07:05:52.269099') AND (`spree_products`.discontinue_on IS NULL or `spree_products`.discontinue_on >= '2020-05-01 07:05:52.269287') AND (`spree_products`.available_on <= '2020-05-01 07:05:52.269272') AND `spree_prices`.`currency` = 'USD' ORDER BY spree_prices.amount asc LIMIT 25 OFFSET 0