SELECT DISTINCT `spree_promotions`.`id` AS alias_0, `spree_promotions`.`id` FROM `spree_promotions` LEFT OUTER JOIN `spree_promotion_actions` ON `spree_promotion_actions`.`deleted_at` IS NULL AND `spree_promotion_actions`.`promotion_id` = `spree_promotions`.`id` WHERE (lower(spree_promotions.code) = 'zxr') AND `spree_promotion_actions`.`id` IS NOT NULL ORDER BY `spree_promotions`.`id` ASC LIMIT 1