SELECT SUM(`spree_adjustments`.`amount`) FROM `spree_adjustments` INNER JOIN `spree_shipments` ON `spree_adjustments`.`adjustable_id` = `spree_shipments`.`id` WHERE `spree_shipments`.`order_id` = 359 AND `spree_adjustments`.`adjustable_type` = 'Spree::Shipment' AND (`spree_adjustments`.`source_type` != 'Spree::TaxRate' OR `spree_adjustments`.`source_type` IS NULL) AND `spree_adjustments`.`eligible` = TRUE