SELECT `spree_products`.* FROM `spree_products` INNER JOIN `friendly_id_slugs` ON `friendly_id_slugs`.`deleted_at` IS NULL AND `friendly_id_slugs`.`sluggable_id` = `spree_products`.`id` AND `friendly_id_slugs`.`sluggable_type` = 'Spree::Product' WHERE (`spree_products`.deleted_at IS NULL or `spree_products`.deleted_at >= '2020-05-01 07:08:23.093117') AND (`spree_products`.discontinue_on IS NULL or `spree_products`.discontinue_on >= '2020-05-01 07:08:23.093347') AND `friendly_id_slugs`.`sluggable_type` = 'Spree::Product' AND `friendly_id_slugs`.`slug` = '1172' ORDER BY `friendly_id_slugs`.`id` DESC LIMIT 1