SELECT DISTINCT `spree_countries`.* FROM `spree_countries` INNER JOIN `spree_zone_members` ON `spree_zone_members`.`zoneable_id` = `spree_countries`.`id` AND `spree_zone_members`.`zoneable_type` = 'Spree::Country' INNER JOIN `spree_zones` ON `spree_zones`.`id` = `spree_zone_members`.`zone_id` INNER JOIN `spree_shipping_method_zones` ON `spree_shipping_method_zones`.`zone_id` = `spree_zones`.`id` INNER JOIN `spree_shipping_methods` ON `spree_shipping_methods`.`deleted_at` IS NULL AND `spree_shipping_methods`.`deleted_at` IS NULL AND `spree_shipping_methods`.`id` = `spree_shipping_method_zones`.`shipping_method_id`