SELECT COUNT(*) FROM (SELECT 1 AS one FROM `spree_stock_movements` INNER JOIN `spree_stock_items` ON `spree_stock_movements`.`stock_item_id` = `spree_stock_items`.`id` WHERE `spree_stock_items`.`deleted_at` IS NULL AND `spree_stock_items`.`stock_location_id` = 371 LIMIT 1 OFFSET 0) subquery_for_count