SELECT `spree_products`.* FROM `spree_products` INNER JOIN `spree_variants` ON `spree_variants`.`deleted_at` IS NULL AND `spree_variants`.`product_id` = `spree_products`.`id` AND `spree_variants`.`is_master` = TRUE INNER JOIN `spree_prices` ON `spree_prices`.`deleted_at` IS NULL AND `spree_prices`.`variant_id` = `spree_variants`.`id` INNER JOIN `friendly_id_slugs` ON `friendly_id_slugs`.`deleted_at` IS NULL AND `friendly_id_slugs`.`sluggable_id` = `spree_products`.`id` AND `friendly_id_slugs`.`sluggable_type` = 'Spree::Product' WHERE `spree_products`.`deleted_at` IS NULL AND (`spree_products`.discontinue_on IS NULL or `spree_products`.discontinue_on >= '2020-05-01 07:07:44.143768') AND (`spree_products`.available_on <= '2020-05-01 07:07:44.143736') AND `friendly_id_slugs`.`sluggable_type` = 'Spree::Product' AND `friendly_id_slugs`.`slug` = 'inactive' ORDER BY `friendly_id_slugs`.`id` DESC LIMIT 1