SELECT `spree_product_properties`.`id` AS t0_r0, `spree_product_properties`.`value` AS t0_r1, `spree_product_properties`.`product_id` AS t0_r2, `spree_product_properties`.`property_id` AS t0_r3, `spree_product_properties`.`created_at` AS t0_r4, `spree_product_properties`.`updated_at` AS t0_r5, `spree_product_properties`.`position` AS t0_r6, `spree_properties`.`id` AS t1_r0, `spree_properties`.`name` AS t1_r1, `spree_properties`.`presentation` AS t1_r2, `spree_properties`.`created_at` AS t1_r3, `spree_properties`.`updated_at` AS t1_r4 FROM `spree_product_properties` LEFT OUTER JOIN `spree_properties` ON `spree_properties`.`id` = `spree_product_properties`.`property_id` WHERE `spree_product_properties`.`product_id` = 1064 AND `spree_properties`.`name` = 'My Property 1' ORDER BY `spree_product_properties`.`position` ASC LIMIT 1