SELECT SUM(`spree_adjustments`.`amount`) FROM `spree_adjustments` WHERE `spree_adjustments`.`adjustable_id` = 41 AND `spree_adjustments`.`adjustable_type` = 'Spree::Order' AND `spree_adjustments`.`eligible` = TRUE AND (`spree_adjustments`.`source_type` != 'Spree::TaxRate' OR `spree_adjustments`.`source_type` IS NULL)