SELECT COUNT(count_column) FROM (SELECT DISTINCT `spree_products`.`id` AS count_column FROM `spree_products` INNER JOIN `spree_variants` ON `spree_variants`.`deleted_at` IS NULL AND `spree_variants`.`product_id` = `spree_products`.`id` AND `spree_variants`.`is_master` = TRUE INNER JOIN `spree_prices` ON `spree_prices`.`deleted_at` IS NULL AND `spree_prices`.`variant_id` = `spree_variants`.`id` WHERE `spree_products`.`deleted_at` IS NULL AND (`spree_products`.discontinue_on IS NULL or `spree_products`.discontinue_on >= '2020-05-01 07:32:11.118262') AND (`spree_products`.available_on <= '2020-05-01 07:32:11.118243') AND (`spree_products`.deleted_at IS NULL or `spree_products`.deleted_at >= '2020-05-01 07:32:11.119398') AND (`spree_products`.discontinue_on IS NULL or `spree_products`.discontinue_on >= '2020-05-01 07:32:11.119639') AND (`spree_products`.available_on <= '2020-05-01 07:32:11.119626') AND (`spree_products`.discontinue_on IS NULL or `spree_products`.discontinue_on >= '2020-05-01 07:32:11.119826') AND (`spree_products`.available_on <= '2020-05-01 07:32:11.119815') AND (spree_prices.amount IS NOT NULL) AND `spree_prices`.`currency` = 'USD' LIMIT 2 OFFSET 0) subquery_for_count