SELECT `spree_product_properties`.* FROM `spree_product_properties` INNER JOIN `spree_properties` ON `spree_properties`.`id` = `spree_product_properties`.`property_id` WHERE `spree_product_properties`.`product_id` = 1095 AND `spree_properties`.`name` = 'the_prop' ORDER BY `spree_product_properties`.`position` ASC LIMIT 1