SELECT `spree_promotions`.* FROM `spree_promotions` INNER JOIN `spree_promotion_rules` ON `spree_promotions`.`id` = `spree_promotion_rules`.`promotion_id` INNER JOIN `spree_product_promotion_rules` ON `spree_promotion_rules`.`id` = `spree_product_promotion_rules`.`promotion_rule_id` WHERE `spree_product_promotion_rules`.`product_id` = 1100 AND `spree_promotions`.`advertise` = TRUE AND (spree_promotions.starts_at IS NULL OR spree_promotions.starts_at < '2020-05-01 07:36:04.715990') AND (spree_promotions.expires_at IS NULL OR spree_promotions.expires_at > '2020-05-01 07:36:04.716341')