SELECT `spree_taxons`.* FROM `spree_taxons` INNER JOIN `spree_products_taxons` ON `spree_taxons`.`id` = `spree_products_taxons`.`taxon_id` INNER JOIN `spree_taxonomies` ON `spree_taxonomies`.`id` = `spree_taxons`.`taxonomy_id` WHERE `spree_products_taxons`.`product_id` = 1153 AND `spree_taxonomies`.`name` = 'Brands' LIMIT 1